import { Component, Input, OnInit } from '@angular/core';
import { GameState } from 'src/app/data/interfaces';
import { state } from '../../data/dummy';

@Component({
  selector: 'app-gamegrid',
  templateUrl: './gamegrid.component.html',
  styleUrls: ['./gamegrid.component.scss']
})
export class GamegridComponent implements OnInit {
  public gameState: GameState = state;

  displayedColumns: string[] = ['Name', 'Score', 'CurrentRound'];

  constructor() { }

  ngOnInit(): void {
  }

}
<h1>{{gameState.title}}</h1>
<table mat-table [dataSource]="gameState.players">

  <!-- Name Column -->
  <ng-container matColumnDef="Name">
    <th mat-header-cell *matHeaderCellDef> Name </th>
    <td mat-cell *matCellDef="let player"> {{player.name}} </td>
  </ng-container>

  <!-- Score Column -->
  <ng-container matColumnDef="Score">
    <th mat-header-cell *matHeaderCellDef> Score </th>
    <td mat-cell *matCellDef="let player"> {{player.score}}</td>
  </ng-container>

  <!-- CurrentRound Column -->
  <ng-container matColumnDef="CurrentRound">
    <th mat-header-cell *matHeaderCellDef> CurrentRound </th>
    <td mat-cell *matCellDef="let player"> {{player.currentRound}} </td>
  </ng-container>


  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
</table>
